import { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

export default function Infoapi() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState("");

  const [listaEmpleados, setListaEmpleados] = useState([]);

  const Guardar = () => {
    axios
      .post("/api/v1/empleados/create", {
        nombre,
        edad,
        pais,
        cargo,
        anios,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const Cargar = () => {
    axios
      .get("/api/v1/empleados/findAll")
      .then((response) => {
        console.log(response);
        setListaEmpleados(response.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    //Cargar();
  }, []);

  return (
    <div className="container">
      <div className="row justify-content-around">
        <div className="col-12 col-sm-6   mb-3 mt-3">
          <h1>Informaci√≥n API REST</h1>
          <div>
            <h3>Obtener todos los empleados</h3>
            <span className="badge bg-secondary">GET</span>{" "}
            <label>/api/v1/findAll/</label>
          </div>
          <div>
            <h3>Crear empleado</h3>
            <span className="badge bg-secondary">POST</span>{" "}
            <label>/api/v1/create/</label>
          </div>
          <div>
            <h3>Actualizar empleado</h3>
            <span className="badge bg-secondary">UPDATE</span>{" "}
            <label>/api/v1/update/</label>
          </div>
          <div>
            <h3>Eliminar empleado</h3>
            <span className="badge bg-secondary">DELETE</span>{" "}
            <label>/api/v1/delete/</label>
          </div>
        </div>
      </div>
    </div>
  );
}
