$("#boton").click(function (e) {
  console.log("hizo click");

  //return false;
});

const primerpaso = () => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      console.log("aca");
      resolve({ data: "ok" });
    }, 3000);
  });
};

let final = primerpaso()
  .then((result) => {
    console.log("info", result.data);
    return "fin";
  })
  .then((result2) => {
    console.log("info2222", result2);
  });

async function prueba() {
  console.log(final);
}
