import './App.css';
import { useState, useEffect } from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';




function App() {
  const [nombre, setNombre] = useState("")
  const [edad, setEdad] = useState(0)
  const [pais, setPais] = useState("")
  const [cargo, setCargo] = useState("")
  const [anios, setAnios] = useState("")

  const [listaEmpleados, setListaEmpleados] = useState([])

  const Guardar = () => {
    axios.post("/create", {
      nombre,
      edad,
      pais,
      cargo,
      anios
    })
      .then((response) => {
        console.log(response)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const Cargar = () => {
    axios.get("/empleados")
      .then((response) => {
        console.log(response)
        setListaEmpleados(response.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  return (
    <div className="App">
      <div className="datos">
        <label>Nombre: <input type="text" onChange={(event) => { setNombre(event.target.value) }} /></label>
        <label>Edad: <input type="number" onChange={(event) => { setEdad(event.target.value) }} /></label>
        <label>Pais: <input type="text" onChange={(event) => { setPais(event.target.value) }} /></label>
        <label>Cargo: <input type="text" onChange={(event) => { setCargo(event.target.value) }} /></label>
        <label>AÃ±os: <input type="text" onChange={(event) => { setAnios(event.target.value) }} /></label>
        <button className='btn btn-success' onClick={Guardar}>Registrar</button>

      </div>
      <button onClick={Cargar}>Cargar Info</button>
      <div className='lista'>
        {
          listaEmpleados.map((val, key) => {
            return <div className=''>{val.nombre}</div>
          })
        }
      </div>
    </div>
  );
}

export default App;
