import { useState, useEffect } from "react";
import axios from "axios";
import Toast from 'react-bootstrap/Toast'
import ToastContainer from 'react-bootstrap/ToastContainer'
import "bootstrap/dist/css/bootstrap.min.css";

export default function Home() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState("");
  const [listaEmpleados, setListaEmpleados] = useState([]);

  const [showA, setShowA] = useState(false);
  const toggleShowA = () => setShowA(!showA);
  const [mensaje, setMensaje] = useState("");

  const Guardar = () => {
    axios
      .post("/api/v1/empleados", {
        nombre,
        edad,
        pais,
        cargo,
        anios,
      }, { validateStatus: false })
      .then((response) => {
        if (response.status === 200) {
          //mostrar mensaje
          setMensaje(response.data.message)
          setShowA(true);
          setNombre("");
          setEdad("");
          setPais("");
          setCargo("");
          setAnios("");
          document.getElementById("crear").reset();
        } else {
          let listaError = "";
          response.data.errors.map((val, key) => {
            listaError += "- " + val.msg + " ";

          })
          setMensaje(listaError);
          setShowA(true);

        }
      })
      .catch((err) => {
        console.log("error", err);
        setMensaje(err.message)
        setShowA(true);
      });
  };


  const Cargar = () => {
    axios
      .get("/api/v1/empleados")
      .then((response) => {
        //console.log(response);
        setListaEmpleados(response.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    Cargar();
  }, []);





  return (
    <div className="container">
      <div className="row justify-content-around">

        <div className="col-12 col-sm-4  mb-3 mt-3">
          <ToastContainer className="p-3" position="top-center">
            <Toast show={showA} onClose={toggleShowA}>
              <Toast.Header>
                <img
                  src="holder.js/20x20?text=%20"
                  className="rounded me-2"
                  alt=""
                />
                <strong className="me-auto">Mensaje</strong>
                <small>Ahora</small>
              </Toast.Header>
              <Toast.Body>{mensaje}</Toast.Body>
            </Toast>
          </ToastContainer>


          <h1 className="text-center">Nuevo empleado</h1>
          <form className="form-horizontal " id="crear">
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="info"
                onChange={(event) => {
                  setNombre(event.target.value);
                }}
              />
              <label>Nombre</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="info"
                onChange={(event) => {
                  setEdad(event.target.value);
                }}
              />
              <label>Edad</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="info"
                onChange={(event) => {
                  setPais(event.target.value);
                }}
              />
              <label>Pais</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="info"
                onChange={(event) => {
                  setCargo(event.target.value);
                }}
              />
              <label>Cargo</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="info"
                onChange={(event) => {
                  setAnios(event.target.value);
                }}
              />
              <label>Años</label>
            </div>
            <div className="d-grid gap-2 col-6 mx-auto">
              <button
                type="button"
                id="boton"
                className="btn btn-primary"
                onClick={Guardar}
              >
                Guardar
              </button>
              <button type="reset" className="btn btn-danger">
                Limpiar
              </button>
            </div>
          </form>
        </div>
        <div className="col-12 col-sm-4   mb-3 mt-3">
          <div className="d-grid gap-2 col-6 mx-auto">
            <button
              type="button"
              id="boton"
              className="btn btn-primary"
              onClick={Cargar}
            >
              Actualizar información
            </button>
          </div>
          <h1 className="text-center">Empleados registrados</h1>
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Nombre</th>
                <th scope="col">Edad</th>
                <th scope="col">Pais</th>
                <th scope="col">Cargo</th>
                <th scope="col">Años</th>
              </tr>
            </thead>
            <tbody>
              {listaEmpleados.map((val, key) => {
                return (
                  <tr key={val.id}>
                    <th>{val.id}</th>
                    <td>{val.nombre}</td>
                    <td>{val.edad}</td>
                    <td>{val.pais}</td>
                    <td>{val.cargo}</td>
                    <td>{val.anios}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

    </div>
  );
}
