import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState("");

  const [listaEmpleados, setListaEmpleados] = useState([]);

  const Guardar = () => {
    axios
      .post("/api/v1/empleados/create", {
        nombre,
        edad,
        pais,
        cargo,
        anios,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const Cargar = () => {
    axios
      .get("/api/v1/empleados/findAll")
      .then((response) => {
        console.log(response);
        setListaEmpleados(response.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    //Cargar();
  }, []);



  return (
    <div className="container">
      <div className="row justify-content-around">
        <div className="col-12 col-sm-4">
          <h1 className="text-center">Nuevo empleado</h1>
          <form className="form-horizontal" id="formulario">
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id=""
                placeholder="info"
                onChange={(event) => {
                  setNombre(event.target.value);
                }}
              />
              <label >Nombre</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id=""
                placeholder="info"
                onChange={(event) => {
                  setEdad(event.target.value);
                }}
              />
              <label >Edad</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id=""
                placeholder="info"
                onChange={(event) => {
                  setPais(event.target.value);
                }}
              />
              <label >Pais</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id=""
                placeholder="info"
                onChange={(event) => {
                  setCargo(event.target.value);
                }}
              />
              <label >Cargo</label>
            </div>
            <div className="form-floating mb-3">
              <input
                type="text"
                className="form-control"
                id=""
                placeholder="info"
                onChange={(event) => {
                  setAnios(event.target.value);
                }}
              />
              <label >Años</label>
            </div>
            <div className="d-grid gap-2 col-6 mx-auto">
              <button
                type="button"
                id="boton"
                className="btn btn-primary"
                onClick={Guardar}
              >
                Guardar
              </button>
              <button type="reset" className="btn btn-danger">
                Limpiar
              </button>
            </div>
          </form>
        </div>
        <div className="col-12 col-sm-4">
          <button
            type="button"
            id="boton"
            className="btn btn-primary"
            onClick={Cargar}
          >
            Actualizar información
          </button>
          <h1 className="text-center">Empleados registrados</h1>
          <table className="table table-dark table-striped">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">nombre</th>
                <th scope="col">edad</th>
                <th scope="col">pais</th>
                <th scope="col">cargo</th>
                <th scope="col">anios</th>
              </tr>
            </thead>
            <tbody>
              {listaEmpleados.map((val, key) => {
                return(
                  <tr>
                  <th>{val.id}</th>
                  <td>{val.nombre}</td>
                  <td>{val.edad}</td>
                  <td>{val.pais}</td>
                  <td>{val.cargo}</td>
                  <td>{val.anios}</td>
                </tr>
                );

              } )}
            </tbody>
          </table>
        </div>
      </div>
      <div className="row justify-content-around">
        <div className="col-12">
          <h1>Información API REST</h1>
          <div>
            <h3>Obtener todos los empleados</h3>
            <span className="badge bg-secondary">GET</span> <label>/api/v1/findAll/</label>
          </div>
          <div>
            <h3>Crear empleado</h3>
            <span className="badge bg-secondary">POST</span> <label>/api/v1/create/</label>
          </div>
          <div>
            <h3>Actualizar empleado</h3>
            <span className="badge bg-secondary">UPDATE</span> <label>/api/v1/update/</label>
          </div>
          <div>
            <h3>Eliminar empleado</h3>
            <span className="badge bg-secondary">DELETE</span> <label>/api/v1/delete/</label>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
